 \"\r\n		document.calc.ln.value=\" ln　\"\r\n		document.calc.log.value=\" log \"\r\n\r\n		if (document.calc.hypf.checked)\r\n		{\r\n			document.calc.sin.value=\"hsin \"\r\n			document.calc.cos.value=\"hcos \"\r\n			document.calc.tan.value=\"htan \"\r\n		}\r\n		else\r\n		{\r\n			document.calc.sin.value=\"sin　\"\r\n			document.calc.cos.value=\"cos　\"\r\n			document.calc.tan.value=\"tan　\"\r\n		}\r\n		\r\n		document.calc.sqr.value=\"x^2　\"\r\n		document.calc.cube.value=\"x^3　\"\r\n		\r\n		document.calc.floor.value=\" 取整 \"\r\n	}\r\n\r\n}\r\n//存储器部分\r\n\r\nfunction clearmemory()\r\n{\r\n	mem=0\r\n	document.calc.memory.value=\"   \"\r\n}\r\n\r\nfunction getmemory()\r\n{\r\n	endNumber=true\r\n	document.calc.display.value=decto(mem,carry)\r\n}\r\n\r\nfunction putmemory()\r\n{\r\n	endNumber=true\r\n	if (document.calc.display.value!=0)\r\n	{\r\n		mem=todec(document.calc.display.value,carry)\r\n		document.calc.memory.value=\" M \"\r\n	}\r\n	else\r\n		document.calc.memory.value=\"   \"\r\n}\r\n\r\nfunction addmemory()\r\n{\r\n	endNumber=true\r\n	mem=parseFloat(mem)+parseFloat(todec(document.calc.display.value,carry))\r\n	if (mem==0)\r\n		document.calc.memory.value=\"   \"\r\n	else\r\n		document.calc.memory.value=\" M \"\r\n}\r\n\r\nfunction multimemory()\r\n{\r\n	endNumber=true\r\n	mem=parseFloat(mem)*parseFloat(todec(document.calc.display.value,carry))\r\n	if (mem==0)\r\n		document.calc.memory.value=\"   \"\r\n	else\r\n		document.calc.memory.value=\" M \"\r\n}\r\n\r\n//十进制转换\r\n\r\nfunction todec(num,oldcarry)\r\n{\r\n	if (oldcarry==10 || num==0) return(num)\r\n	var neg=(num.charAt(0)==\"-\")\r\n	if (neg) num=num.substr(1)\r\n	var newnum=0\r\n	for (var index=1;index<=num.length;index++)\r\n		newnum=newnum*oldcarry+hexnum.indexOf(num.charAt(index-1))\r\n	if (neg)\r\n		newnum=-newnum\r\n	return(newnum)\r\n}\r\n\r\nfunction decto(num,newcarry)\r\n{\r\n	var neg=(num<0)\r\n	if (newcarry==10 || num==0) return(num)\r\n	num=\"\"+Math.abs(num)\r\n	var newnum=\"\"\r\n	while (num!=0)\r\n	{\r\n		newnum=hexnum.charAt(num%newcarry)+newnum\r\n		num=Math.floor(num/newcarry)\r\n	}\r\n	if (neg)\r\n		newnum=\"-\"+newnum\r\n	return(newnum)\r\n}\r\n\r\n//表达式解析\r\n\r\nfunction parse(string)\r\n{\r\n	if (string.match(/^(.*d[+-*/\\\\%^&|x<])?([+-]?[0-9a-f.]+)([+-*/\\\\%^&|x<])([+-]?[0-9a-f.]+)\$/))\r\n		return(RegExp.\$1+cypher(RegExp.\$2,RegExp.\$3,RegExp.\$4))\r\n	else\r\n		return(string)\r\n}\r\n\r\n//数学运算和位运算\r\n\r\nfunction cypher(left,join,right)\r\n{\r\n	left=todec(left,carry)\r\n	right=todec(right,carry)\r\n	if (join==\"+\")\r\n		return(decto(parseFloat(left)+parseFloat(right),carry))\r\n	if (join==\"-\")\r\n		return(decto(left-right,carry))\r\n	if (join==\"*\")\r\n		return(decto(left*right,carry))\r\n	if (join==\"/\" && right!=0)\r\n		return(decto(left/right,carry))\r\n	if (join==\"%\")\r\n		return(decto(left%right,carry))\r\n	if (join==\"&\")\r\n		return(decto(left&right,carry))\r\n	if (join==\"|\")\r\n		return(decto(left|right,carry))\r\n	if (join==\"^\")\r\n		return(decto(Math.pow(left,right),carry))\r\n\r\n	if (join==\"x\")\r\n		return(decto(left^right,carry))\r\n	if (join==\"<\")\r\n		return(decto(left<<right,carry))\r\n	alert(\"除数不能为零\")\r\n	return(left)\r\n}\r\n\r\n//函数计算\r\n\r\nfunction funcalc(fun,num)\r\n{\r\n	with(Math)\r\n	{\r\n		if (fun==\"pi\")\r\n			return(PI)\r\n		if (fun==\"e\")\r\n			return(E)\r\n\r\n		if (fun==\"abs\")\r\n			return(abs(num))\r\n		if (fun==\"ceil\")\r\n			return(ceil(num))\r\n		if (fun==\"round\")\r\n			return(round(num))\r\n\r\n		if (fun==\"floor\")\r\n			return(floor(num))\r\n		if (fun==\"deci\")\r\n			return(num-floor(num))\r\n\r\n\r\n		if (fun==\"ln\" && num>0)\r\n			return(log(num))\r\n		if (fun==\"exp\")\r\n			return(exp(num))\r\n		if (fun==\"log\" && num>0)\r\n			return(log(num)*LOG10E)\r\n		if (fun==\"expdec\")\r\n			return(pow(10,num))\r\n\r\n		\r\n		if (fun==\"cube\")\r\n			return(num*num*num)\r\n		if (fun==\"cubt\")\r\n			return(pow(num,1/3))\r\n		if (fun==\"sqr\")\r\n			return(num*num)\r\n		if (fun==\"sqrt\" && num>=0)\r\n			return(sqrt(num))\r\n\r\n		if (fun==\"!\")\r\n			return(factorial(num))\r\n\r\n		if (fun==\"recip\" && num!=0)\r\n			return(1/num)\r\n		\r\n		if (fun==\"dms\")\r\n			return(dms(num))\r\n		if (fun==\"deg\")\r\n			return(deg(num))\r\n\r\n		if (fun==\"~\")\r\n			return(~num)\r\n	\r\n		if (angle==\"d\")\r\n		{\r\n			if (fun==\"sin\")\r\n				return(sin(degressToRadians(num)))\r\n			if (fun==\"cos\")\r\n				return(cos(degressToRadians(num)))\r\n			if (fun==\"tan\")\r\n				return(tan(degressToRadians(num)))\r\n\r\n			if (fun==\"arcsin\" && abs(num)<=1)\r\n				return(radiansToDegress(asin(num)))\r\n			if (fun==\"arccos\" && abs(num)<=1)\r\n				return(radiansToDegress(acos(num)))\r\n			if (fun==\"arctan\")\r\n				return(radiansToDegress(atan(num)))\r\n		}\r\n		else\r\n		{\r\n			if (fun==\"sin\")\r\n				return(sin(num))\r\n			if (fun==\"cos\")\r\n				return(cos(num))\r\n			if (fun==\"tan\")\r\n				return(tan(num))\r\n\r\n			if (fun==\"arcsin\" && abs(num)<=1)\r\n				return(asin(num))\r\n			if (fun==\"arccos\" && abs(num)<=1)\r\n				return(acos(num))\r\n			if (fun==\"arctan\")\r\n				return(atan(num))\r\n		}\r\n	\r\n		if (fun==\"hypsin\")\r\n			return((exp(num)-exp(0-num))*0.5)\r\n		if (fun==\"hypcos\")\r\n			return((exp(num)+exp(-num))*0.5)\r\n		if (fun==\"hyptan\")\r\n			return((exp(num)-exp(-num))/(exp(num)+exp(-num)))\r\n\r\n		if (fun==\"ahypsin\" | fun==\"hypcos\" | fun==\"hyptan\")\r\n		{\r\n			alert(\"对不起,公式还没有查到!\")\r\n			return(num)\r\n		}\r\n		\r\n		alert(\"超出函数定义范围\")\r\n		return(num)\r\n	}\r\n}\r\n\r\nfunction factorial(n)\r\n{\r\n	n=Math.abs(parseInt(n))\r\n	var fac=1\r\n	for (;n>0;n-=1)\r\n		fac*=n\r\n	return(fac)\r\n}\r\n\r\nfunction dms(n)\r\n{\r\n	var neg=(n<0)\r\n	with(Math)\r\n	{	\r\n		n=abs(n)\r\n		var d=floor(n)\r\n		var m=floor(60*(n-d))\r\n		var s=(n-d)*60-m\r\n	}\r\n	var dms=d+m/100+s*0.006\r\n	if (neg) \r\n		dms=-dms\r\n	return(dms)\r\n}\r\n\r\nfunction deg(n)\r\n{\r\n	var neg=(n<0)\r\n	with(Math)\r\n	{\r\n		n=abs(n)\r\n		var d=floor(n)\r\n		var m=floor((n-d)*100)\r\n		var s=(n-d)*100-m\r\n	}\r\n	var deg=d+m/60+s/36\r\n	if (neg) \r\n		deg=-deg\r\n	return(deg)\r\n}\r\n\r\nfunction degressToRadians(degress)\r\n{\r\n	return(degress*Math.PI/180)\r\n}\r\n\r\nfunction radiansToDegress(radians)\r\n{\r\n	return(radians*180/Math.PI)\r\n}\r\n\r\n//界面\r\n//-->\r\n</SCRIPT>\r\n<body bgcolor=\"fcfff7\" leftmargin=\"0\" topmargin=\"0\" marginwidth=\"0\">\r\n<br>\r\n<DIV align=center>\r\n<FORM name=calc>\r\n<TABLE width=650  height=250 bgcolor=\"#E6F2E7\" style=\"border-width:1px 1px 1px 1px;border-style:solid;border-color:#8AB78A;\">\r\n  <TBODY>\r\n  <TR>\r\n    <TD height=50 bgcolor=\"#E6F2E7\">\r\n      <TABLE width=100%>\r\n        <TBODY>       \r\n<TR><TD align=center><FONT color=#205001>www.hao123.com </FONT></TD></TR>\r\n <TR><TD height=\"35\" align=center>\r\n           <INPUT name=display readOnly size=50 value=0 style=\" font-size:18px;\"></TD>\r\n </TR></TBODY></TABLE></TD>\r\n  </TR>\r\n  <TR>\r\n    <TD bgcolor=\"#E6F2E7\">\r\n      <TABLE width=100% style=\"border-width:1px 0px 0px 0px;border-style:solid;border-color:#8AB78A;\">\r\n        <TBODY>\r\n        <TR>\r\n          <TD style=\"color:#205001;\"><INPUT name=carry onclick=inputChangCarry(16) \r\n            type=radio> 十六进制 <INPUT CHECKED name=carry \r\n            onclick=inputChangCarry(10) type=radio> 十进制 <INPUT name=carry \r\n            onclick=inputChangCarry(8) type=radio> 八进制 <INPUT name=carry \r\n            onclick=inputChangCarry(2) type=radio> 二进制 </TD>\r\n          <TD width=\"187\" style=\"color:#205001;\"><INPUT CHECKED name=angle \r\n            onclick=\"inputChangAngle(''d'')\" type=radio value=d> \r\n            角度制 \r\n              <INPUT \r\n            name=angle onclick=\"inputChangAngle(''r'')\" type=radio value=r> \r\n              弧度制        </TD>\r\n          </TR></TBODY></TABLE>\r\n      <TABLE width=100%>\r\n        <TBODY>\r\n        <TR>\r\n          <TD width=170 style=\"color:#205001;\"><INPUT name=s